import './Formulario.css'
import CampoTexto from '../CampoTexto/CampoTexto'
import Botao from '../Botao/Botao'
import { useState } from 'react'

const Formulario = (props) => {

    const [nome, setNome] = useState('')
    const [mensagem, setMensagem] = useState('')
    const [telefone, setTelefone] = useState('')
    const [email, setEmail] = useState('')


    const Salvar = (evento) => {
        evento.preventDefault()
        props.aoColaboradorCadastrado({
            nome,
            mensagem,
            telefone,
            email
        })
    }
    

    return(
        <section className='formulario'>
            <form onSubmit={Salvar}>
                <h2>Preencha os dados abaixo</h2>
                <CampoTexto obrigatorio={true} label="Nome Completo" valor={nome}aoAlterado={valor => setNome(valor)}/>

                <CampoTexto obrigatorio={true} label="E-mail para contato" valor={email}aoAlterado={valor => setEmail(valor)}/>

                <CampoTexto label="Telefone" valor={telefone}aoAlterado={valor => setTelefone(valor)}/>

                <CampoTexto obrigatorio={true} label="Mensagem" valor={mensagem}aoAlterado={valor => setMensagem(valor)}/>
                <Botao>
                    Enviar
                </Botao>
            </form>
        </section>
    )


}

export default Formulario
