import './Formulario.css'
import CampoTexto from '../CampoTexto/CampoTexto'
import Lista from '../Lista/Lista'
import Botao from '../Botao/Botao'
import { useState } from 'react'

const Formulario = (props) => {

    const areas = [
        'Programação',
        'Front-End',
        'Devops',
        'Mobile'
    ]

    const [nome, setNome] = useState('')
    const [site, setSite] = useState('')
    const [imagem, setImagem] = useState('')
    const [email, setEmail] = useState('')
    const [area, setArea] = useState('')


    const Salvar = (evento) => {
        evento.preventDefault()
        props.aoColaboradorCadastrado({
            nome,
            cargo,
            imagem,
            area
        })
    }
    

    return(
        <section className='formulario'>
            <form onSubmit={Salvar}>
                <h2>Preencha os dados abaixo</h2>
                <CampoTexto obrigatorio={true} label="Nome" placeholder="Digite seu nome" valor={nome}aoAlterado={valor => setNome(valor)}/>

                <CampoTexto obrigatorio={true} label="o que deseja?" valor={site}aoAlterado={valor => setSite(valor)}/>

                <CampoTexto label="Modelo do Site" placeholder="Envie aqui o modelo do site" valor={imagem}aoAlterado={valor => setImagem(valor)}/>

                <CampoTexto obrigatorio={true} label="E-mail para contato" placeholder="Digite seu E-mail" valor={email}aoAlterado={valor => setEmail(valor)}/>

                <Lista label="Qual Área?" itens={areas}valor={area}aoAlterado={valor => setArea(valor)}/>
                <Botao>
                    Enviar
                </Botao>
            </form>
        </section>
    )


}

export default Formulario
