import './Formulario.css'
import CampoTexto from '../CampoTexto/CampoTexto'
import Lista from '../Lista/Lista'
import Botao from '../Botao/Botao'
import { useState } from 'react'

const Formulario = (props) => {

    const areas = [
        'Programação',
        'Front-End',
        'Devops',
        'Mobile'
    ]

    const [nome, setNome] = useState('')
    const [site, setSite] = useState('')
    const [imagem, setImagem] = useState('')
    const [email, setEmail] = useState('')
    const [area, setArea] = useState('')


    const Salvar = (evento) => {
        evento.preventDefault()
        props.aoColaboradorCadastrado({
            nome,
            site,
            imagem,
            email,
            area
        })
    }
    

    return(
        <section className='formulario'>
            <form onSubmit={Salvar}>
                <fieldset>
                <legend>Preencha os dados abaixo</legend>

                <CampoTexto obrigatorio={true} label="Nome Completo" valor={nome}aoAlterado={valor => setNome(valor)}/>

                <CampoTexto obrigatorio={true} label="O que deseja?" valor={site}aoAlterado={valor => setSite(valor)}/>

                <CampoTexto label="Modelo do Site" valor={imagem}aoAlterado={valor => setImagem(valor)}/>

                <CampoTexto obrigatorio={true} label="E-mail para contato" valor={email}aoAlterado={valor => setEmail(valor)}/>

                <Lista label="Qual Área?" itens={areas}valor={area}aoAlterado={valor => setArea(valor)}/>
                <Botao>
                    Enviar
                </Botao>
                </fieldset>
            </form>
        </section>
    )


}

export default Formulario
