import './Formulario.css'
import CampoTexto from '../CampoTexto/CampoTexto'
import Botao from '../Botao/Botao'
import { useState } from 'react'

const Formulario = (props) => {

    const [nome, setNome] = useState('')
    const [mensagem, setMensagem] = useState('')
    const [email, setEmail] = useState('')


    const Salvar = (evento) => {
        evento.preventDefault()
        props.aoColaboradorCadastrado({
            nome,
            mensagem,
            email
        })
    }

    var close = document.getElementsByClassName("closebtn");
var i;

    for (i = 0; i < close.length; i++) {
        // When someone clicks on a close button
        close[i].onclick = function(){
      
          // Get the parent of <span class="closebtn"> (<div class="alert">)
          var div = this.parentElement;
      
          // Set the opacity of div to 0 (transparent)
          div.style.opacity = "0";
      
          // Hide the div after 600ms (the same amount of milliseconds it takes to fade out)
          setTimeout(function(){ div.style.display = "none"; }, 600);
        }
      }
    

    return(
        
        <section className='formulario'>    
            <form onSubmit={Salvar}>
                <h2>Entre em contato comigo !!</h2>
                <CampoTexto obrigatorio={true} label="Nome Completo" valor={nome}aoAlterado={valor => setNome(valor)}/>

                <CampoTexto obrigatorio={true} label="E-mail" valor={email}aoAlterado={valor => setEmail(valor)}/>

                <CampoTexto obrigatorio={true} label="Mensagem de amor" valor={mensagem}aoAlterado={valor => setMensagem(valor)}/>

                <Botao>
                    Enviar
                </Botao>

                <div class="alert">
                    <span class="closebtn" onclick="this.parentElement.style.display='none';">&times;</span>
                    This is an alert box.
                </div>

            </form>
        </section>
        
    )


}

export default Formulario
